import React, { useEffect, useState } from 'react'
import {
    Table,
    TableHeader,
    TableCell,
    TableBody,
    TableRow,
    TableFooter,
    TableContainer,
    Button,
    Pagination,
} from '@windmill/react-ui'
import ModalFormRequest from './ModalFormRequest'

export default function TableInProgress() {
    // setup pages control for every table
    const [pageTable, setPageTable] = useState(1)

    // setup data for every table
    const [dataTable, setDataTable] = useState([])
    const [isModalOpen, setIsModalOpen] = useState(false)

    // pagination setup
    const resultsPerPage = 10
    const totalResults = dataTable.length

    // pagination change control
    function onPageChangeTable(p) {
        setPageTable(p)
    }

    function openModal() {
        setIsModalOpen(true)
    }

    function closeModal() {
        setIsModalOpen(false)
    }

    // on page change, load new sliced data
    // here you would make another server request for new data
    useEffect(() => {
        try {
            // assetsServices.getAllITAsset().then(data => {
            //     const response = data.concat([])
            //     setDataTable(data, response.slice((pageTable - 1) * resultsPerPage, pageTable * resultsPerPage))
            // })
        } catch (error) {
            console.log(error)
        }
    }, [pageTable])

  return (
      <>
          <TableContainer className="mb-8">
              <Table>
                  <TableHeader>
                      <tr>
                          <TableCell>Asset Name</TableCell>
                          <TableCell>Category</TableCell>
                          <TableCell>Type</TableCell>
                          <TableCell>Serial Number</TableCell>
                          <TableCell className="text-center">Status</TableCell>
                          <TableCell>Actions</TableCell>
                      </tr>
                  </TableHeader>
                  <TableBody>
                      {/* {dataTable.map((asset) => ( */}
                          <TableRow className="dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600">
                              <TableCell>
                                  {/* <Link className="text-blue-500" to='/app/assets/detail'>{asset.data.assetName}</Link> */}
                              </TableCell>
                              <TableCell>
                                  {/* {asset.data.category} */}
                              </TableCell>
                              <TableCell>
                                  {/* {asset.data.type} */}
                              </TableCell>
                              <TableCell>
                                  {/* {asset.data.serialNumber} */}
                              </TableCell>
                              <TableCell className="text-center" >
                                  {/* <Badge type={asset.data.status === 'Standby' ? 'success' : 'primary'}></Badge> */}
                              </TableCell>
                              <TableCell>
                                  <div className="flex items-center space-x-4">
                                      <Button layout="link" size="icon" aria-label="Edit" onClick={() => openModal()}>
                                          <EditIcon className="w-5 h-5" aria-hidden="true" color="#7e3af2" />
                                      </Button>
                                      {selected === "Archived" || selected === "Draft" ?
                                          (<Button layout="link" size="icon" aria-label="Delete" onClick={() => handleDelete()}>
                                              <TrashIcon className="w-5 h-5" aria-hidden="true" color='#c81e1e' />
                                          </Button>)
                                          :
                                          (<Button layout="link" size="icon" aria-label="Archive" onClick={() => handleArchive()}>
                                              <ArchiveIcon className="w-5 h-5" aria-hidden="true" color="#7e3af2" />
                                          </Button>)
                                      }
                                  </div>
                              </TableCell>
                          </TableRow>
                      {/* ))} */}
                  </TableBody>
              </Table>
              <TableFooter>
                  <Pagination
                      totalResults={totalResults}
                      resultsPerPage={resultsPerPage}
                      onChange={onPageChangeTable}
                      label="Table navigation"
                  />
              </TableFooter>
          </TableContainer>
          <ModalFormRequest isModalOpen={isModalOpen} closeModal={closeModal} />
      </>
  )
}
