import React, { useEffect, useState } from 'react'
import { Card, CardBody, Button, Input, Label, Select } from '@windmill/react-ui'

import { PageTitle } from '../../components'
import { useForm } from 'react-hook-form'
import { assetsServices } from '../../services/assets'
import Swal from 'sweetalert2'
import { Timestamp } from 'firebase/firestore'
import { Link, useHistory } from 'react-router-dom'
import { sitesServices } from '../../services/sites'

function ITAsset() {
    const { id } = useParams();
    const [asset, setAsset] = useState([])
    const data = {
        // site: asset.data.siteLabel,
        // salesOrder: value.salesOrder,
        // brand: value.brand,
        // model: value.model,
        // serialNumber: value.serialNumber.toUpperCase(),
        // category: value.category,
        // information: {
        //     operatingSystem: value.operatingSystem,
        //     processor: value.processor,
        //     ram: value.ram.toUpperCase().replace(/\s+/g, ''),
        //     storageCapacity: value.storageCapacity.toUpperCase().replace(/\s+/g, ''),
        //     storageType: value.storageType
        // },
        visibility: 'Unarchived',
        status: 'Standby',
    }
    const { register, handleSubmit, reset } = useForm({ defaultValues: data })
    const [site, setSite] = useState([])
    const [siteLabel, setSiteLabel] = useState('')
    const history = useHistory()

    const handleChange = (e) => {
        let index = e.nativeEvent.target.selectedIndex;
        let label = e.nativeEvent.target[index].text;
        setSiteLabel(label)
    }

    const onSubmit = (value) => {
        const dataAsset = {
            site: siteLabel,
            salesOrder: value.salesOrder,
            brand: value.brand,
            model: value.model,
            serialNumber: value.serialNumber,
            category: value.category,
            information: {
                operatingSystem: value.operatingSystem,
                processor: value.processor,
                ram: value.ram,
                storageCapacity: value.storageCapacity,
                storageType: value.storageType
            },
            visibility: 'Unarchived',
            status: 'Standby',
            createdAt: Timestamp.now()
        }

        try {
            // if (id == null) {
            Swal.fire({
                title: 'Do you want to save the New IT Asset?',
                showDenyButton: false,
                showCancelButton: true,
                confirmButtonText: 'Save',
            }).then((result) => {
                /* Read more about isConfirmed, isDenied below */
                if (result.isConfirmed) {
                    assetsServices.add(dataAsset)
                    Swal.fire('Saved!', '', 'success')
                        .then(() => {
                            window.location.reload(history.replace('/app/assets'))

                        })
                }
            })
            // } else {
            // Swal.fire({
            //     title: 'Do you want to save the changes?',
            //     showDenyButton: true,
            //     showCancelButton: true,
            //     confirmButtonText: 'Save',
            //     denyButtonText: `Don't save`,
            // }).then((result) => {
            //     /* Read more about isConfirmed, isDenied below */
            //     if (result.isConfirmed) {
            //         assetsServices.update(id, {
            //             assetName: value.assetName,
            //             category: value.category,
            //             type: value.type,
            //             serialNumber: value.serialNumber,
            //             storageCapacity: value.storageCapacity,
            //             storageType: value.storageType,
            //             status: value.status,
            //             visibility: value.visibility,
            //         })
            //         // console.log("Edit : ",dataAsset);
            //         Swal.fire('Saved!', '', 'success')
            //             .then(() => window.location.reload())
            //     } else if (result.isDenied) {
            //         Swal.fire('Changes are not saved', '', 'info')
            //     }
            // })
            // }
        } catch (err) {
            alert(err)
        }
    }

    // useEffect(() => {
    //     reset(data)
    // }, [reset, data])

    useEffect(() => {
        try {
            assetsServices.getById(id).then(data => {
                setAsset(data)
            })
        } catch (error) {
            console.log(error)
        }
    }, [id])


    useEffect(() => {
        try {
            sitesServices.getAll().then(data => {
                setSite(data)
            })
        } catch (error) {
            console.log(error)
        }
    }, [])

    console.log(asset);

    return (
        <>
            <PageTitle>Form IT Asset</PageTitle>

            <form onSubmit={handleSubmit(onSubmit)}>
