import React from 'react';

import { SearchIcon } from '../../assets/icons';
import TableTechnician from './TableTechnician';
import { Input, Button } from '@windmill/react-ui';
import ModalFormTechnician from './ModalFormTechnician';

function TabTechnician() {
    const [isModalOpen, setIsModalOpen] = useState(false)

    function openModal() {
        setIsModalOpen(true)
    }

    function closeModal() {
        setIsModalOpen(false)
    }
  return (
      <>
          {/* <!-- Search input --> */}
          <div className="flex justify-center flex-1 mb-3">
              <div className="relative w-full focus-within:text-purple-500">
                  <div className="absolute inset-y-0 flex items-center pl-2">
                      <SearchIcon className="w-4 h-4 text-purple-600" aria-hidden="true" />
                  </div>
                  <Input
                      className="pl-8 text-gray-700"
                      placeholder="Search for data"
                      aria-label="Search"
                  />
              </div>
          </div>

          {/* Button Add */}
          <div className='flex justify-end mx-4 mb-3'>
              <Button>
                  Add New
                  <span className="ml-2" aria-hidden="true">
                      +
                  </span>
              </Button>
          </div>

          {/* Table Data */}
          <TableTechnician />

          <ModalFormTechnician/>
      </>
  )
}

export default TabTechnician
