import { addDoc, collection, getDocs, updateDoc, deleteDoc, setDoc } from "firebase/firestore";
import { db } from "../utils/firebase";

export const rolesServices = {
    // Get a list of roles from database
    async getAll() {
        const col = collection(db, 'roles');
        const snapshot = await getDocs(col);
        const list = snapshot.docs.map(doc => doc.data());
        return list;
    },

    // Create a list of roles from database
    async create() {
        const col = collection(db, 'roles');
        const snapshot = await addDoc(col);
        const add = snapshot.docs.map(doc => ({
            id: doc.id,
            data: doc.data()
        }));
        return add;
    },

    // Update a list of roles from database
    async update(id) {
        const col = collection(db, 'roles')
        const snapshot = await updateDoc(col);
        const edit = snapshot.docs.map(doc => doc.data());
        return edit;        
    },

    // Delete a list of roles from database
    async delete(id) {

        const col = collection(db, 'roles')
        const snapshot = await deleteDoc(col);
        const remove = snapshot.docs.map(doc => doc.data());
        return remove;             
    },

    // Delete All list of roles from database
    async deleteAll() {
        return
    },
}
