import React, { useEffect, useState } from "react"

import {
  Table,
  TableHeader,
  TableCell,
  TableBody,
  TableRow,
  TableFooter,
  TableContainer,
  Badge,
  Button,
  Pagination,
} from "@windmill/react-ui"
import { ArchiveIcon, EditIcon, TrashIcon } from "../../assets/icons"

import { Link } from "react-router-dom"
import { assetsServices } from "../../services/assets"
import Swal from "sweetalert2"
import ModalFormITAsset from "./ModalFormITAsset"
// make a copy of the data, for the second table

function TableITAsset({ selected }) {
  const [response, setResponse] = useState([])

  // setup pages control for every table
  const [pageTable, setPageTable] = useState(1)

  // setup data for every table
  const [dataTable, setDataTable] = useState([])
  const [isModalOpen, setIsModalOpen] = useState(false)
  // const [textBlue, setTextBlue] = useState(false)
  // const [archived, setArchived] = useState(false)
  const [assetId, setAssetId] = useState(null)
  const [assetData, setAssetData] = useState([])

  useEffect(() => {
    try {
      assetsServices.getAllITAsset().then((data) => {
        setResponse(data)
      })
    } catch (error) {
      console.log(error)
    }
  }, [])

  // pagination setup
  const resultsPerPage = 10
  //   const totalResults = response?.length;
  if (response?.length && 0) {
    console.log(response.length)
  }

  // pagination change control
  function onPageChangeTable(p) {
    setPageTable(p)
  }

  function openModal(value) {
    setIsModalOpen(true)
    setAssetId(value.id)
    setAssetData(value.data)
  }

  function closeModal() {
    setIsModalOpen(false)
  }

  const handleArchive = (id) => {
    try {
      Swal.fire({
        text: "Do you want to move in Archive?",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, archive it!",
      }).then((result) => {
        if (result.isConfirmed) {
          assetsServices.update(id, { visibility: "Archived" })
          Swal.fire(
            "Archived!",
            "Your file has been archived.",
            "success"
          ).then(() => window.location.reload())
        }
      })
    } catch (err) {
      alert(err)
    }
  }

  const handleDelete = (id) => {
    try {
      Swal.fire({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, delete it!",
      }).then((result) => {
        if (result.isConfirmed) {
          assetsServices.delete(id)
          Swal.fire("Deleted!", "Your file has been deleted.", "success").then(
            () => window.location.reload()
          )
        }
      })
    } catch (err) {
      alert(err)
    }
  }

  // on page change, load new sliced data
  // here you would make another server request for new data
  useEffect(() => {
    setDataTable(
      response.slice(
        (pageTable - 1) * resultsPerPage,
        pageTable * resultsPerPage
      )
    )
  }, [response, pageTable])

  return (
    <>
      <TableContainer className="mb-8">
        <Table>
          <TableHeader>
            <tr>
              <TableCell>Asset Name</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Type</TableCell>
              <TableCell>Serial Number</TableCell>
              <TableCell className="text-center">Status</TableCell>
              <TableCell>Actions</TableCell>
            </tr>
          </TableHeader>
          <TableBody>
            {dataTable.map((asset) => (
              <TableRow
                className="dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600"
                key={asset.id}
              >
                <TableCell>
                  <Link
                    className="text-blue-500"
                    to={`/app/assets/detail/${asset.id}`}
                  >
                    {asset.data.assetName}
                  </Link>
                </TableCell>
                <TableCell>{asset.data.category}</TableCell>
                <TableCell>{asset.data.type}</TableCell>
                <TableCell>{asset.data.serialNumber}</TableCell>
                <TableCell className="text-center">
                  <Badge
                    type={
                      asset.data.status === "Standby" ? "success" : "primary"
                    }
                  >
                    {asset.data.status}
                  </Badge>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-4">
                    <Button
                      layout="link"
                      size="icon"
                      aria-label="Edit"
                      onClick={() => openModal(asset)}
                    >
                      <EditIcon
                        className="w-5 h-5"
                        aria-hidden="true"
                        color="#7e3af2"
                      />
                    </Button>
                    {selected === "Archived" || selected === "Draft" ? (
                      <Button
                        layout="link"
                        size="icon"
                        aria-label="Delete"
                        onClick={() => handleDelete(asset.id)}
                      >
                        <TrashIcon
                          className="w-5 h-5"
                          aria-hidden="true"
                          color="#c81e1e"
                        />
                      </Button>
                    ) : (
                      <Button
                        layout="link"
                        size="icon"
                        aria-label="Archive"
                        onClick={() => handleArchive(asset.id)}
                      >
                        <ArchiveIcon
                          className="w-5 h-5"
                          aria-hidden="true"
                          color="#7e3af2"
                        />
                      </Button>
                    )}
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        {response?.length !== 0 && (
          <TableFooter>
            <Pagination
              totalResults={response.length}
              resultsPerPage={resultsPerPage}
              onChange={onPageChangeTable}
              label="Table navigation"
            />
          </TableFooter>
        )}
      </TableContainer>
      <ModalFormITAsset
        isModalOpen={isModalOpen}
        closeModal={closeModal}
        id={assetId}
        data={assetData}
      />
    </>
  )
}

export default TableITAsset
