import { addDoc, collection, getDocs, query, updateDoc, where } from "firebase/firestore";
import { db } from "../utils/firebase";

export const assetsServices = {
    // Get a reference to the cities collection
    async getITAsset(archive) {
        const col = collection(db, 'assets');
        const query = query(col, where('category', '==', 'Laptop'))
        const snapshot = await getDocs(query);
        const list = snapshot.docs.map(doc => doc.data());
        return list;
    },

    async getNonITAsset(archive) {
        const col = collection(db, 'assets');
        const query = query(col, where('category', '==', 'Printer'))
        const snapshot = await getDocs(query);
        const list = snapshot.docs.map(doc => doc.data());
        return list;
    }
}
