import React from "react";
import {
  Button, Card, CardBody, TableContainer, Table, TableCell, TableBody, TableRow, TableHeader, Input
} from "@windmill/react-ui";
import { ArrowLeftIcon } from "../../assets/icons";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import Swal from "sweetalert2";

function Permission() {
  const { register, handleSubmit } = useForm()
  const history = useHistory();

  const onSubmit = (value) => {
    console.log(value);
    Swal.fire({
      title: 'Do you want to save the changes?',
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: 'Save',
      denyButtonText: `Don't save`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        // rolesServices.update(id, dataRole)
        Swal.fire('Saved!', '', 'success')
          .then(() => window.location.reload()).then(() => history.goBack())
      } else if (result.isDenied) {
        Swal.fire('Changes are not saved', '', 'info')
          .then(() => history.goBack())
      }
    })
  }
  return (
    <>
      <div className="mt-5">
        <Button
          layout="outline"
          iconLeft={ArrowLeftIcon}
          onClick={history.goBack}
        >
          Go Back
        </Button>
      </div>

      <Card className="my-8 shadow-md">
        <CardBody>
          <p className="mb-4 font-semibold text-gray-600 dark:text-gray-300">
            Role Admin
          </p>
          <form onSubmit={handleSubmit(onSubmit)}>
            <TableContainer className="mb-8">
              <Table>
                <TableHeader>
                  <tr className="text-center">
                    <TableCell className="text-left">Permissions</TableCell>
                    <TableCell>All Access</TableCell>
                    <TableCell>View</TableCell>
                    <TableCell>Add</TableCell>
                    <TableCell>Edit</TableCell>
                    <TableCell>Delete</TableCell>
                  </tr>
                </TableHeader>
                <TableBody>
                  {data.map((data, i) => (
                    <TableRow key={i} className="text-center dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600" >
                      <TableCell className="text-left" name={data.permission} id={data.permission} {...register("permission")}>
                        {data.permission}
                      </TableCell>
                      <TableCell>
                        <Input type="checkbox" name="checkAll" id="checkAll" className="border-gray-300 dark:border-gray-600" />
                      </TableCell>
                      <TableCell>
                        <Input type="checkbox" name="view" id="view" {...register("view")} className="border-gray-300 dark:border-gray-600" />
                      </TableCell>
                      <TableCell>
                        <Input type="checkbox" name="add" id="add" {...register("add")} className="border-gray-300 dark:border-gray-600" />
                      </TableCell>
                      <TableCell>
                        <Input type="checkbox" name="edit" id="edit" {...register("edit")} className="border-gray-300 dark:border-gray-600" />
                      </TableCell>
                      <TableCell>
                        <Input type="checkbox" name="delete" id="delete" {...register("delete")} className="border-gray-300 dark:border-gray-600" />
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <Button block size="large" type="submit">
              Save
            </Button>
          </form>
        </CardBody>
      </Card>
    </>
  );
}

export default Permission;

const data = [
  {
    permission: "Request",
    access: {
      view: false,
      add: false,
      edit: false,
      delete: false
    }
  },
  {
    permission: "Asset",
    access: {
      view: false,
      add: false,
      edit: false,
      delete: false
    }
  },
]
