import { collection, getDocs, updateDoc } from "firebase/firestore";
import { db } from "../utils/firebase";

const col = collection(db, 'roles')

export const rolesServices = {
    // Get a list of roles from database
    async getAll() {
        const roleCol = collection(db, 'roles');
        const roleSnapshot = await getDocs(roleCol);
        const roleList = roleSnapshot.docs.map(doc => doc.data());
        return roleList;
    },

    // Create a list of roles from database
    async create(e) {
        const roleCol = collection(db, 'roles');
        const roleSnapshot = await updateDoc(roleCol);
        const roleList = roleSnapshot.docs.map(doc => ({
            id: doc.id,
            data: doc.data()
        }));
        return roleList;
    },

    // Update a list of roles from database
    async update() {
