import React from "react";
import {
  Button, Card, CardBody, TableContainer, Table, TableCell, TableBody, TableRow, TableHeader, Input
} from "@windmill/react-ui";
import { ArrowLeftIcon } from "../../assets/icons";
import { useHistory } from "react-router-dom";

function Permission() {
  const history = useHistory();



  return (
    <>
      <div className="mt-5">
        <Button
          layout="outline"
          iconLeft={ArrowLeftIcon}
          onClick={history.goBack}
        >
          Go Back
        </Button>
      </div>

      <Card className="my-8 shadow-md">
        <CardBody>
          <p className="mb-4 font-semibold text-gray-600 dark:text-gray-300">
            Permissions
          </p>


          <TableContainer className="mb-8">
            <Table>
              <TableHeader>
                <tr className="text-center">
                  <TableCell className="text-left">Permissions</TableCell>
                  <TableCell>All Access</TableCell>
                  <TableCell>View</TableCell>
                  <TableCell>Add</TableCell>
                  <TableCell>Edit</TableCell>
                  <TableCell>Delete</TableCell>
                </tr>
              </TableHeader>
              <TableBody>
                {data.map((data) => (
                <TableRow className="text-center dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600" >
                    <TableCell className="text-left">
                      {data.permission}
                  </TableCell>
                  <TableCell>
                    <Input type="checkbox" name="checkAll" className="border-gray-300 dark:border-gray-600" />
                  </TableCell>
                  <TableCell>
                      <Input type="checkbox" name="view" checked={data.access.view} className="border-gray-300 dark:border-gray-600" />
                  </TableCell>
                  <TableCell>
                      <Input type="checkbox" name="add" checked={data.access.add} className="border-gray-300 dark:border-gray-600" />
                  </TableCell>
                  <TableCell>
                      <Input type="checkbox" name="edit" checked={data.access.edit} className="border-gray-300 dark:border-gray-600" />
                  </TableCell>
                  <TableCell>
                      <Input type="checkbox" name="delete" checked={data.access.delete} className="border-gray-300 dark:border-gray-600" />
                  </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>


        </CardBody>
      </Card>
    </>
  );
}

export default Permission;

const data = [
  {
    permission: "Request",
    access: {
      view: false,
      add: true,
      edit: false,
      delete: false
    }
  },
  {
    permission: "Asset",
    access: {
      view: false,
      add: false,
      edit: false,
      delete: false
    }
  },
]
