import { collection, deleteDoc, getDocs, updateDoc } from "firebase/firestore";
import { db } from "../utils/firebase";

export const usersServices = {
    // Get a list of users from database
    async getAll() {
        const col = collection(db, 'roles');
        const get = await getDocs(col);
        const list = get.docs.map(doc => ({
            id: doc.id,
            data: doc.data()
        }));
        return list;
    },

    async delete(id) {
        const col = collection(db, 'users')
        const remove = await deleteDoc(doc(col, id));
        return remove;
