import React, { useState } from 'react';

import { AddIcon, SearchIcon } from '../../assets/icons';
import { Input, Button } from '@windmill/react-ui';
import ModalFormUser from './ModalFormUser';
import TableUser from './TableUser';
import { db } from './../../utils/firebase';
import { collection, getDocs } from 'firebase/firestore';

function TabUser() {
    const [isModalOpen, setIsModalOpen] = useState(false)

    function openModal() {
        setIsModalOpen(true)
    }

    function closeModal() {
        setIsModalOpen(false)
    }

     const dbStore = db
    // Get a list of cities from your database
    async function getUsers(dbStore) {
        const citiesCol = collection(db, 'users');
        const citySnapshot = await getDocs(citiesCol);
        const cityList = citySnapshot.docs.map(doc => doc.data());
        return cityList;
    }

    console.log("haiii", getUsers)

    return (
        <>
            {/* <!-- Search input --> */}
            <div className="flex justify-center flex-1 mb-3">
                <div className="relative w-full focus-within:text-purple-500">
                    <div className="absolute inset-y-0 flex items-center pl-2">
                        <SearchIcon className="w-4 h-4 text-purple-600" aria-hidden="true" />
                    </div>
                    <Input
                        className="pl-8 text-gray-700"
                        placeholder="Search for data"
                        aria-label="Search"
                    />
                </div>
            </div>

            {/* Button Add */}
            <div className='flex justify-end mx-4 mb-3'>
                <Button iconLeft={AddIcon} onClick={openModal}>
                    <span>Add New</span>
                </Button>
            </div>

            {/* Table Data */}
            <TableUser />

            <ModalFormUser isModalOpen={isModalOpen} closeModal={closeModal} />
        </>
    )
}

export default TabUser
