import { addDoc, collection, getDocs, updateDoc, deleteDoc, doc } from "firebase/firestore";
import { db } from "../utils/firebase";

export const rolesServices = {
    // Get a list of roles from database
    async getAll() {
        const col = collection(db, 'roles');
        const get = await getDocs(col);
        // const list = get.docs.map(doc => doc.data());
        return get;
    },

    // Create a list of roles from database
    async create(values) {
        const col = collection(db, 'roles');
        const add = await addDoc(col, values);
        return add;
    },

    // Update a list of roles from database
    async update() {
        const col = collection(db, 'roles')
        const edit = await updateDoc(col);
        return edit;        
    },

    // Delete a list of roles from database
    async delete(id) {
        const col = collection(db, 'roles')
        const remove = await deleteDoc(doc(col, id));
        return remove;             
    },

    // Delete All list of roles from database
    async deleteAll() {
        return
    },
}
