import { addDoc, collection, getDocs, query, updateDoc, where } from "firebase/firestore";
import { db } from "../utils/firebase";

export const assetsServices = {
    // Get a list of roles from database
    async getAll() {
        const col = collection(db, 'assets');
        const snapshot = await getDocs(col);
        const list = snapshot.docs.map(doc => doc.data());
        return list;
    },

    // Create a reference to the cities collection
    async getITAsset(archived) {
        const col = collection(db, 'assets');
        // const categoryQuery = query(col, where('category', '==', 'Laptop'))
        // const archivedQuery = query(col, where('archived', '==', false))
        const snapshot = await getDocs(col);
        const list = snapshot.docs.map(doc => doc.data());
        console.log("arc",archived)
        return list;
    }
}
