import React, { useEffect, useState } from 'react'
import { Card, CardBody, Button, Input, Label, Select } from '@windmill/react-ui'

import { PageTitle } from '../../components'
import { useForm } from 'react-hook-form'
import { assetsServices } from '../../services/assets'
import Swal from 'sweetalert2'
import { Timestamp } from 'firebase/firestore'
import { useHistory } from 'react-router-dom'
import { sitesServices } from '../../services/sites'

function ITAsset(id) {
    const { register, handleSubmit, reset } = useForm({})
    const [site, setSite] = useState([])
    const history = useHistory()

    const onSubmit = (value) => {
        const dataAsset = {
            assetId: Math.random(),
            site: value.site,
            salesOrder: value.salesOrder,
            brand: value.brand,
            model: value.model,
            serialNumber: value.serialNumber,
            category: value.category,
            information: {
                operatingSystem: value.operatingSystem,
                processor: value.processor,
                ram: value.ram,
                storageCapacity: value.storageCapacity,
                storageType: value.storageType
            },
            visibility: 'Unarchived',
            status: 'Standby',
            createdAt: Timestamp.now()
        }

        try {
            // if (id == null) {
            Swal.fire({
                title: 'Do you want to save the New IT Asset?',
                showDenyButton: false,
                showCancelButton: true,
                confirmButtonText: 'Save',
            }).then((result) => {
                /* Read more about isConfirmed, isDenied below */
                if (result.isConfirmed) {
                    assetsServices.add(dataAsset)
                    Swal.fire('Saved!', '', 'success')
                        .then(() => {
                            window.location.reload()
                            history.replace('/app/assets')
                        })
                }
            })
            // } else {
            // Swal.fire({
            //     title: 'Do you want to save the changes?',
            //     showDenyButton: true,
            //     showCancelButton: true,
            //     confirmButtonText: 'Save',
            //     denyButtonText: `Don't save`,
            // }).then((result) => {
            //     /* Read more about isConfirmed, isDenied below */
            //     if (result.isConfirmed) {
            //         assetsServices.update(id, {
            //             assetName: value.assetName,
            //             category: value.category,
            //             type: value.type,
            //             serialNumber: value.serialNumber,
            //             storageCapacity: value.storageCapacity,
            //             storageType: value.storageType,
            //             status: value.status,
            //             visibility: value.visibility,
            //         })
            //         // console.log("Edit : ",dataAsset);
            //         Swal.fire('Saved!', '', 'success')
            //             .then(() => window.location.reload())
            //     } else if (result.isDenied) {
            //         Swal.fire('Changes are not saved', '', 'info')
            //     }
            // })
            // }
        } catch (err) {
            alert(err)
        }
    }

    // useEffect(() => {
    //     reset(data)
    // }, [reset, data])

    useEffect(() => {
        try {
            sitesServices.getAll().then(data => {
                setSite(data)
            })
        } catch (error) {
            console.log(error)
        }
    }, [third])


    return (
        <>
            <PageTitle>Form IT Asset</PageTitle>

            <form onSubmit={handleSubmit(onSubmit)}>
                <div className="grid gap-6 mb-8 md:grid-cols-2">
                    <Card>
                        <CardBody>
                            <p className="mb-4 font-semibold text-gray-600 dark:text-gray-300">Asset Detail</p>
                            <Label>
                                <span>Site<small className='text-red-600'>*</small></span>
                                <Select className="mt-1" required {...register("site")}>
                                    <option value="" >-- Choose one --</option>
                                    {site.map((site, i) => (
                                        <option key={i} value={site.data.id}>{site.data.name}</option>
                                    ))}
                                    {/* <option value="bni" >BANK NEGARA INDONESIA (PRESERO)</option>
                                    <option value="bri" >BANK RAKYAT INDONESIA (PRESERO)</option>
                                    <option value="tmin" >TOYOTA MANUFACTURING INDONESIA</option> */}
                                </Select>
                            </Label>
                            <Label className="mt-3">
                                <span>SO Number<small className='text-red-600'>*</small></span>
                                <Input
                                    className="mt-1"
                                    placeholder="Type here..."
                                    required
                                    {...register("salesOrder")}
                                />
                            </Label>
                            <div className="grid col-gap-3 lg:grid-cols-2">
                                <Label className="mt-3">
                                    <span>Brand<small className='text-red-600'>*</small></span>
                                    <Input
                                        className="mt-1"
                                        placeholder="Type here..."
                                        required
                                        {...register("brand")}
                                    />
                                </Label>
                                <Label className="mt-3">
                                    <span>Model<small className='text-red-600'>*</small></span>
                                    <Input
                                        className="mt-1"
                                        placeholder="Type here..."
                                        required
                                        {...register("model")}
                                    />
                                </Label>

                                <Label className="mt-3">
                                    <span>Serial Number<small className='text-red-600'>*</small></span>
                                    <Input
                                        className="mt-1"
                                        placeholder="Type here..."
                                        required
                                        {...register("serialNumber")}
                                    />
                                </Label>
                                <Label className="mt-3">
                                    <span>Category<small className='text-red-600'>*</small></span>
                                    <Select className="mt-1" required {...register("category")}>
                                        <option value="" >-- Choose one --</option>
                                        <option value="Laptop" >Laptop</option>
                                        <option value="Desktop" >Desktop</option>
