import React, { useEffect, useState } from 'react'

import {
    Table,
    TableHeader,
    TableCell,
    TableBody,
    TableRow,
    TableFooter,
    TableContainer,
    Badge,
    Button,
    Pagination
} from '@windmill/react-ui'
import { ArchiveIcon, EditIcon, TrashIcon } from '../../assets/icons'

import { Link } from 'react-router-dom'
import { assetsServices } from '../../services/assets'
import Swal from 'sweetalert2'
import ModalFormITAsset from './ModalFormITAsset'
// make a copy of the data, for the second table

function TableITAsset({ selected }) {

    // setup pages control for every table
    const [pageTable, setPageTable] = useState(1)

    // setup data for every table
    const [dataTable, setDataTable] = useState([])
    const [isModalOpen, setIsModalOpen] = useState(false)
    // const [textBlue, setTextBlue] = useState(false)
    // const [archived, setArchived] = useState(false)
    const [assetId, setAssetId] = useState(null)
    const [assetData, setAssetData] = useState([])

    // pagination setup
    const resultsPerPage = 10
    const totalResults = dataTable.length

    // pagination change control
    function onPageChangeTable(p) {
        setPageTable(p)
    }

    function openModal(value) {
        setIsModalOpen(true)
        setAssetId(value.id)
        setAssetData(value.data)
    }

    function closeModal() {
        setIsModalOpen(false)
    }

    const handleArchive = (id) => {
        try {
            Swal.fire({
                text: "Do you want to move in Archive?",
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Yes, archive it!'
            }).then((result) => {
                if (result.isConfirmed) {
                    assetsServices.update(id, { "visibility": "Archived", })
                    Swal.fire(
                        'Archived!',
                        'Your file has been archived.',
                        'success',
                    ).then(() => window.location.reload())
                }
            })
        } catch (err) {
            alert(err)
        }
    }

    const handleDelete = (id) => {
        try {
            Swal.fire({
                title: 'Are you sure?',
                text: "You won't be able to revert this!",
                icon: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Yes, delete it!'
            }).then((result) => {
                if (result.isConfirmed) {
                    assetsServices.delete(id)
                    Swal.fire(
                        'Deleted!',
                        'Your file has been deleted.',
                        'success',
                    ).then(() => window.location.reload())
                }
            })
        } catch (err) {
            alert(err)
        }
    }

    function Action(id) {
        if (selected === "Archived" || "Draft") {
            return (
                <Button layout="link" size="icon" aria-label="Delete" onClick={() => handleDelete(id)}>
                    <TrashIcon className="w-5 h-5" aria-hidden="true" color='#c81e1e' />
                </Button>
            )
        }
        return (
            <Button layout="link" size="icon" aria-label="Archive" onClick={() => handleArchive(id)}>
                <ArchiveIcon className="w-5 h-5" aria-hidden="true" color="#7e3af2" />
            </Button>
        )
    }

    // on page change, load new sliced data
    // here you would make another server request for new data
    useEffect(() => {
        try {
            assetsServices.getAllITAsset().then(data => {
                const response = data.concat([])
                setDataTable(data, response.slice((pageTable - 1) * resultsPerPage, pageTable * resultsPerPage))
            })
        } catch (error) {
            console.log(error)
        }
    }, [pageTable])

    // const cont = [
    //     { id: 1, value: 'asd' },
    //     { id: 2, value: 'dsa' },
    // ]
    // const condata = cont.filter(dt => {
    //     return dt.id == 1
    // })
    // condata.map((data) => (console.log(data)))


    const filterArchive = dataTable.filter((asset) => {
        return asset.data.visibility = selected
    })
    filterArchive.map((data) => (console.log(data)))
    // console.log(dataTable);


    return (
        <>
            <TableContainer className="mb-8">
                <Table>
                    <TableHeader>
                        <tr>
                            <TableCell>Asset ID</TableCell>
                            <TableCell>Asset Name</TableCell>
                            <TableCell>Category</TableCell>
                            <TableCell>Type</TableCell>
                            <TableCell>Serial Number</TableCell>
                            <TableCell className="text-center">Status</TableCell>
                            <TableCell >Visibility</TableCell>
                            <TableCell>Actions</TableCell>
                        </tr>
                    </TableHeader>
                    <TableBody>
                        {dataTable.map((asset) => (
                            <TableRow className="dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600" key={asset.id}>
                                <TableCell>
                                    {asset.data.assetId}
                                </TableCell>
                                <TableCell>
                                    <Link className="text-blue-500" to='/app/assets/detail'>{asset.data.assetName}</Link>
                                </TableCell>
                                <TableCell>
                                    {asset.data.category}
                                </TableCell>
                                <TableCell>
                                    {asset.data.type}
                                </TableCell>
                                <TableCell>
                                    {asset.data.serialNumber}
                                </TableCell>
                                <TableCell className="text-center" >
                                    <Badge type={asset.data.status === 'Standby' ? 'success' : 'primary'}>{asset.data.status}</Badge>
                                </TableCell>
                                <TableCell>
                                    {asset.data.visibility}
                                </TableCell>
                                <TableCell>
                                    <div className="flex items-center space-x-4">
                                        <Button layout="link" size="icon" aria-label="Edit" onClick={() => openModal(asset)}>
                                            <EditIcon className="w-5 h-5" aria-hidden="true" color="#7e3af2" />
                                        </Button>
                                        <Action id={asset.id} />
                                        {/* {selected !== 'Unarchived' ?
                                            (<Button layout="link" size="icon" aria-label="Delete" onClick={() => handleDelete(asset.id)}>
                                                <TrashIcon className="w-5 h-5" aria-hidden="true" color='#c81e1e' />
                                            </Button>)
                                            :
                                            (<Button layout="link" size="icon" aria-label="Archive" onClick={() => handleArchive(asset.id)}>
                                                <ArchiveIcon className="w-5 h-5" aria-hidden="true" color="#7e3af2" />
                                            </Button>)
                                        } */}
                                    </div>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
                <TableFooter>
                    <Pagination
                        totalResults={totalResults}
                        resultsPerPage={resultsPerPage}
                        onChange={onPageChangeTable}
                        label="Table navigation"
                    />
                </TableFooter>
            </TableContainer>
            <ModalFormITAsset isModalOpen={isModalOpen} closeModal={closeModal} id={assetId} data={assetData} />
        </>
    )
}

export default TableITAsset
