import React, { useState } from 'react'
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Label } from '@windmill/react-ui'
import { addDoc, collection, Timestamp } from 'firebase/firestore'
import { db } from '../../utils/firebase'
import { useForm } from 'react-hook-form'

function ModalFormRole({ closeModal, isModalOpen }) {
    const initial = {
        roleName: '',
        description: '',
        permissions: {},
        createdAt: Timestamp.now()
    }

    const [roleData, setRoleData] = useState(initial)

    const handleSubmit =  (e) => {

        // Add a new document in collection "cities"
        db.collection("roles").doc("TesID").set({
            roleName: "Role Baru",
            description: "Descriptionnya apa",
        })
            .then(() => {
                alert("Document successfully written!");
            })
            .catch((error) => {
                console.error("Error writing document: ", error);
            });
    }

    // const { control, handleSubmit,  } = useForm({
    //     defaultValues: {
    //         firstName: '',
    //         select: {}
    //     }
    // });
    const onSubmit = data => console.log(data);

    return (
        <>
            <Modal isOpen={isModalOpen} onClose={closeModal}>
                <ModalHeader>Form Data Role</ModalHeader>
                <form>
                    <ModalBody>
                        <Label>
                            <span>Role Name<small className='text-red-600'>*</small></span>
                            <Input
                                name="roleName"
                                id="roleName"
                                className="mt-1"
                                placeholder="Type here..."
                            // value={roleData.roleName}
                            />
                        </Label>
                        <Label className="mt-3">
                            <span>Description<small className='text-red-600'>*</small></span>
                            <Input
                                name="description"
                                id="description"
                                className="mt-1"
                                placeholder="Type here..."
                            // value={roleData.description}
                            />
                        </Label>
                    </ModalBody>
                    <ModalFooter>
                        {/* I don't like this approach. Consider passing a prop to ModalFooter
           * that if present, would duplicate the buttons in a way similar to this.
           * Or, maybe find some way to pass something like size="large md:regular"
           * to Button
           */}
                        <div className="hidden sm:block">
                            <Button layout="outline" onClick={closeModal}>
                                Cancel
                            </Button>
                        </div>
                        <div className="hidden sm:block">
                            <Button onClick={handleSubmit}>Save</Button>
                        </div>
                        <div className="block w-full sm:hidden">
                            <Button block size="large" layout="outline" onClick={closeModal}>
                                Cancel
                            </Button>
                        </div>
                        <div className="block w-full sm:hidden">
                            <Button block size="large" onClick={handleSubmit}>
                                Save
                            </Button>
                        </div>
                    </ModalFooter>
                </form>
            </Modal>
        </>
    )
}

export default ModalFormRole
